package com.example.iw20252026merca_esi.views;

import com.vaadin.flow.component.button.Button;
import com.vaadin.flow.component.dependency.CssImport;
import com.vaadin.flow.component.html.Image;
import com.vaadin.flow.component.html.Span;
import com.vaadin.flow.component.orderedlayout.HorizontalLayout;
import com.vaadin.flow.component.orderedlayout.VerticalLayout;
import com.vaadin.flow.router.Route;
import com.vaadin.flow.server.auth.AnonymousAllowed;
import com.vaadin.flow.router.PageTitle;
import com.vaadin.flow.router.Menu;

@PageTitle("figma")
@AnonymousAllowed
@Route(value = "figma")
@Menu(title = "figma")
public class Figma extends VerticalLayout {

    public Figma() {
        setWidthFull();
        setPadding(false);
        setSpacing(false);
        getStyle().set("background-color", "#073b7a");
        getStyle().set("color", "white");
        getStyle().set("text-align", "center");
        getStyle().set("font-family", "Arial, sans-serif");
        HorizontalLayout header = new HorizontalLayout();
        header.setWidthFull();
        header.setJustifyContentMode(JustifyContentMode.BETWEEN);
        header.getStyle().set("padding", "10px");
        Button homeButton = new Button("HOME");
        homeButton.getStyle().set("color", "white");
        homeButton.getStyle().set("background", "none");
        homeButton.getStyle().set("border", "none");
        Button aboutButton = new Button("ABOUT");
        aboutButton.getStyle().set("color", "white");
        aboutButton.getStyle().set("background", "none");
        aboutButton.getStyle().set("border", "none");
        Button contactButton = new Button("CONTACT");
        contactButton.getStyle().set("color", "white");
        contactButton.getStyle().set("background", "none");
        contactButton.getStyle().set("border", "none");
        header.add(homeButton, aboutButton, contactButton);
        Span title = new Span("DAILY DAIRY");
        title.getStyle().set("font-size", "48px");
        title.getStyle().set("font-weight", "bold");
        Span subtitle = new Span("DIVE INTO MY COLLECTION — A MILK CAP A DAY, EVERY DAY.");
        subtitle.getStyle().set("font-size", "18px");
        subtitle.getStyle().set("margin-bottom", "20px");
        add(header, title, subtitle);
        HorizontalLayout gridLayout = new HorizontalLayout();
        gridLayout.setWidthFull();
        gridLayout.setJustifyContentMode(JustifyContentMode.CENTER);
        gridLayout.getStyle().set("flex-wrap", "wrap");
        for (int i = 1; i <= 10; i++) {
            VerticalLayout itemLayout = new VerticalLayout();
            itemLayout.setAlignItems(Alignment.CENTER);
            itemLayout.getStyle().set("padding", "10px");
            itemLayout.getStyle().set("border", "1px solid white");
            Image image = new Image("data:image/jpeg;base64,", "Milk Cap " + i);
            Span itemLabel = new Span("ITEM " + i);
            Span itemDesc = new Span("Description for item " + i);
            itemLayout.add(image, itemLabel, itemDesc);
            gridLayout.add(itemLayout);
        }
        add(gridLayout);
        Span footer = new Span("MADE BY STEVE");
        footer.getStyle().set("font-size", "18px");
        footer.getStyle().set("margin-top", "20px");
        Span links = new Span("INSTA EBAY FACEBOOK EMAIL");
        links.getStyle().set("font-size", "24px");
        links.getStyle().set("font-weight", "bold");
        Span bottomBar = new Span("DAILY DAIRY ©2025");
        bottomBar.getStyle().set("font-size", "18px");
        bottomBar.getStyle().set("margin-top", "20px");
        add(footer, links, bottomBar);
    }
}
